import React ,{Component} from 'react';
import "./ColorBox.css";
import { CopyToClipboard } from "react-copy-to-clipboard";
import {Link} from "react-router-dom";
import chroma from "chroma-js";
import {withStyles } from "@material-ui/styles";


const styles = {
	ColorBox:{
		width: "20%",
		margin: "0 auto",
		display: "inline-block",
		position: "relative",
		cursor: "pointer",
		marginBottom:"-3.5px",
		"&:hover button":{
			opacity: 1
		},
		height: 
		  props => props.showingFullPalette ? "25%" : "50%",
	},
	copyText: {
		color : 
			props => chroma(props.background).luminance() >= 0.7 ? "black" : "white"
	},
	colorName:{
		color : 
			props => chroma(props.background).luminance() <= 0.08 ? "white" : "black"
	},
	seeMore :{
		background: "rgba(255,255,255,0.3)",
		position: "absolute",
		border:"none",
		right: "0px",
		bottom :"0px",
		width: "60px",
		height: "30px",
		textAlign: "center",
		lineHeight: "30px",
		textTransform: "uppercase",
		color : 
			props => chroma(props.background).luminance() >= 0.7 ? "rgba(0,0,0,0.7)": "white"
	},
	copyButton :{
		width: "100px",
		height: "30px",
		display: "inline-block",
		position: "absolute",
		top:"50%",
		left: "50%",
		marginLeft:" -50px",
		marginTop:"-15px",
		textAlign: "center",
		outline: "none",
		background: "rgba(255,255,255,0.3)",
		fontSize: "1rem",
		lineHeight: "0px",
		textTransform: "uppercase",
		border:"none",
		textDecoration: "none",
		opacity : 0,
		color : 
			props => chroma(props.background).luminance() >= 0.7 ? "rgba(0,0,0,0.7)": "white"

	},
	boxContent:{
		position: "absolute",
		width: "100%",
		left: "0px",
		bottom: "0px",
		padding: "10px",
		color: "black",
		letterSpacing: "1px",
		textTransform: "uppercase",
		fontSize: "12px"
	},
	copyOverlay:{
		opacity: "0",
		zIndex: "0;",
		width: "100%",
		height: "100%",
		transition: "transform 0.6 ease-in-out",
		transform :"scale(0.1)"
	},
	showOverlay:{
		opacity: "1",
		transform:"scale(50)",
		zIndex: "10",
		position: "absolute"
	},
	copyMessage:{
		position: "fixed",
		left: "0",
		top: "0",
		bottom: "0",
		right: "0",
		display: "flex",
		alignItems: "center",
		justifyContent: "center",
		fontSize: "4rem",
		transform: "scale(0.1)",
		opacity: "0",
		color: "white",
		flexDirection: "column",
		"& h1" :{
			fontWeight: "400",
			textShadow: "1px 2px black",
			background: "rgba(255,255,255,0.2)",
			width: "100%",
			textAlign: "center",
			marginBottom:" 0",
			textTransform: "uppercase",
			padding: "1rem "
		}
	},
	showMessage :{
		opacity: "1",
		transform: "scale(1)",
		zIndex: "25",
		transition : "all 0.4 ease-in-out",
		transitionDelay: "0.3s"
	},



};

class ColorBox extends Component{
	constructor(props){
		super(props);
		this.state ={
			copied :false
		}
		this.changeCopyState =this.changeCopyState.bind(this);
	}
	changeCopyState(){
		this.setState({ copied : true } , () =>{
			setTimeout(() => this.setState({copied : false}) , 1200);
		});

	}
	render(){

		const {name,background,paletteId,id,showLink ,classes ,showingFullPalette} = this.props;
		const {copied} 			 = this.state;
		// const isDarkColor = chroma(background).luminance() <=0.08;
		// const isLightColor = chroma(background).luminance() >= 0.7;
		 return(
			<CopyToClipboard text={background} onCopy={this.changeCopyState}>
				<div 
					style={{background : background}}
					className= {classes.ColorBox} > 
					<div 
						style={{background : background}} 
						className={`${classes.copyOverlay} ${copied && classes.showOverlay}`}
					/>
					<div className={`${classes.copyMessage} ${copied && classes.showMessage } `}>
						<h1> copied! </h1>
							<p className={classes.copyText}>
							{this.props.background} 
						</p>
					</div>
						<div > 
							<div className={classes.boxContent}>
								<span className ={classes.colorName}> {name} </span>
							</div>
							<button className={classes.copyButton}> Copy </button>
		 				</div>
		 			{showingFullPalette && ( 
			 			<Link 
			 				to= {`/palette/${paletteId}/${id}`} 
			 				onClick={ (e) => e.stopPropagation()}
			 			>
		 					<span className={classes.seeMore}> MORE </span>
		 				</Link> 
	 				)}
	 			</div>
 			</CopyToClipboard>
		);
	}
}
export default withStyles(styles)(ColorBox);
